import { SIGN_IN } from '../actions/types';
const initialState = {
    status:{}
}

export default function(state = initialState, action) {
  switch(action.type) {
    case SIGN_IN:
      return {
        ...state,
        status: action.status === "success"?true:false,
        email:action.email,
    };

    default:
      return state;
  }
}
